import React, { useState } from "react";
import "./DocRankUI.css";
import DocRankUI from "./FileUpload";

const useColorAssignment = (initialColors) => {
    const [colors, setColors] = useState(initialColors);

    const assignColor = () => {
        if (colors.length === 0) return null;

        const randomIndex = Math.floor(Math.random() * colors.length);
        const color = colors[randomIndex];
        setColors((prevColors) => prevColors.filter((c, index) => index !== randomIndex));

        return color;
    };

    return assignColor;
};


function FileInput({ setIsLoading, setFileText, setFileNames, closefileinput }) {



    return (
        <div className="mac-book-pro142-group9">


                <h4>File Upload</h4>
                <i class="fa-solid fa-cloud-arrow-up"></i> <FileUpload setIsLoading={setIsLoading} setFileText={setFileText} closefileinput={closefileinput} setFileNames={setFileNames} />

            <div className="mac-book-pro142-donebutton" onClick={() => closefileinput()}>
                <span className="mac-book-pro142-text12">
                    <span>DONE</span>
                </span>
            </div>
            <div className="mac-book-pro142-frame2"></div>
        </div>
    );
}

export default DocRankUI;
