import React from "react";
import "./stylesSR.css";
import "./bost.scss"
function SearchResult({ name, score, matchedWords, showthispdf }) {
  function sendname(nn) {
    showthispdf(nn);
  }
  return (
    <div className="search-result">
      <div className="name">{name}
        <div className="score">{score.toFixed(4)}</div>
      </div>

      <div className="matched-words">
        {matchedWords.length > 0 && (
          <div className="matched-words-container">
            <div className="matched-words-title">Matched Words:</div>
            <div className="matched-words-scroll">
              {matchedWords.map((word) => (
                <div className="matched-word" key={word["word"]}>
                  {word["word"]} {word["count"]}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
      <div className="showmorepdfdiv">
        <button
          onClick={() => {
            sendname(name);
          }}
          className="showmorepdf"
        >
          show
        </button>
      </div>
    </div>

  );
}

export default function SearchResults({ results, showthispdf }) {
  return (
    <div className="search-results-container">
      {results.map((result, index) => (


        // <SearchResult key={index} showthispdf={showthispdf} {...result} />


<li data-qa="searchResultItem">
                <div class="job-tile job-grid-item" data-bind="
    css: { 'job-grid-item--active': job.isActive, 'job-grid-item--focus': hasFocus }
">
    <a class="job-grid-item__link" data-bind="
        attr: { href: getJobUrl(job.id) },
        click: () => openJobPreview(job.id),
        hasFocus: hasFocus
    " href="https://hdpc.fa.us2.oraclecloud.com/hcmUI/CandidateExperience/en/sites/LateralHiring/job/116053/?selectedFlexFieldsFacets=%252522AttributeChar8%25257CSkillset%252522">
        <div class="job-grid-item__content">
            <div class="job-grid-item__header" data-bind="style: gridItemHeaderAdditionalStyles">
                <search-result-item-header params="
                        job: job,
                        jobHeadingConfig: jobHeadingConfig,
                    "><div class="job-tile__header">
    <span class="job-tile__title" data-bind="text: job.title">Internal Audit, Global Banking &amp; Markets, Risk Management (1LOD)Vice President -Birmingham</span>


    <div class="job-tile__subheader">

        <span>

<span data-bind="html: primaryLocation">Birmingham, West Midlands, England, United Kingdom</span>

        </span>

        <span>
            <i18n params="key: 'search.posted-on-label'"><span data-bind="i18n: {key: key, tokens: tokens}">Posted on <span data-i18n-token="posteddate" data-bind="text: job.postedDate">03/21/2024</span></span>
</i18n>
        </span>

    </div>

</div>
</search-result-item-header>
            </div>

            <div class="job-grid-item__body" data-bind="style: gridItemBodyAdditionalStyles">

                <div>
                    <p class="job-grid-item__description" data-bind="text: job.shortDescription">Internal Audit, GBM, Risk Management (1LOD) Vice President - Birmingham office. </p>
                </div>

            </div>
        </div>
    </a>

    <div class="job-grid-item__actions">
        <button class="favourite-star job-tile__favorited job-grid-item__favourite-star" data-bind="
                click: toggleFavourite,
                clickBubble: false,
                css: { 'favourite-star--active': isFavourite },
                i18nAttr: { title: !isFavourite() ? 'favourite.a11y.pin-job' : 'favourite.a11y.unpin-job',
                'aria-label': !isFavourite() ? 'favourite.a11y.pin-job' : 'favourite.a11y.unpin-job' }
            " title="Add Job to My Job Selections" aria-label="Add Job to My Job Selections"></button>

    </div>
</div>

            </li>
      ))}
    </div>

    // <table className="tablesr">
    //   <tbody>

    //       {results.map((result, index) => (
    //         <tr className="trsr" key={index}>

    //             <SearchResult showthispdf={showthispdf} {...result} />

    //         </tr>
    //       ))}

    //   </tbody>
    // </table>
  );
}
