      });

      const jaccardSimilarity = intersectionSize / unionSize;

      const matchedWords = Object.keys(intersection).map((word) => {
        return { word: word, count: intersection[word] };
      });

      return {
        name: doc,
        score: jaccardSimilarity,
        matchedWords: matchedWords,
      };
    });

    results.sort((a, b) => {
