          </div>

          <div class="terms col-sm-3"> 
          <div>
                  <h1>Terms</h1>
                  <CheckboxList
                    terms={querymap}
                    checkedTerms={checkedTerms}
                    setCheckedTerms={setCheckedTerms}
                    onCheckedTerms={handleCheckedTerms}
                  />
                </div>

          </div>
            </div>

        </div>



        <div class="fotter">


        </div>
    </Provider>
  );
