import React, { useState } from "react";
import "./Box.css";
// import styles from './stylenew.css';


// Custom hook for synchronous color assignment
const useColorAssignment = (initialColors) => {
    const [colors, setColors] = useState(initialColors);

    const assignColor = () => {
        if (colors.length === 0) return null;

        const randomIndex = Math.floor(Math.random() * colors.length);
        const color = colors[randomIndex];
        setColors((prevColors) => prevColors.filter((c, index) => index !== randomIndex));

        return color;
    };

    return assignColor;
};


function TextInput({ queryterms, setqueryterms, setcolorMap, brightColors, setbrightColors,setshowtextinput }) {
    const [inputValue, setInputValue] = useState("");
    const assignColor = useColorAssignment(brightColors);

    const handleInputOnChange = (e) => {
        setInputValue(e.target.value);
    };

    const handleInputOnKeyPress = (e) => {
        if (e.key === "Enter") {
            addTerms(inputValue);
        }
    };

    const handleAddButtonClick = () => {
        addTerms(inputValue);
    };

    const handleRemoveTerm = (index) => {
        setqueryterms((prevTerms) => prevTerms.filter((_, i) => i !== index));
    };

    const addTerms = (value) => {
        const newTerms = value
            .split(";")
            .filter((term) => term.trim() !== "")
            .map((term) => term.trim().toLowerCase());

        setqueryterms((prevTerms) => [...prevTerms, ...newTerms]);

        setcolorMap((prevColorMap) => {
            const newColorMap = { ...prevColorMap };

            for (const term of newTerms) {
                if (!newColorMap[term]) {
                    const randomColor = assignColor();
                    if (randomColor) {
                        newColorMap[term] = randomColor;
                    }
                }
            }
            console.log(newColorMap);
            return newColorMap;
        });

        setInputValue("");
    };



    return (

        // <div className="box">
        //   <div className="input-container">
        //     <input
        //       type="text"
        //       value={inputValue}
        //       onChange={handleInputOnChange}
        //       onKeyPress={handleInputOnKeyPress}
        //       placeholder="Enter queryterms separated by semicolon"
        //       className="input-field"
        //     />
        //     <button onClick={handleAddButtonClick} className="add-button">
        //       Add
        //     </button>
        //   </div>
        //   <div className="queryterms-container">
        //     {queryterms.map((term, index) => (
        //       <div key={index} className="term">
        //         <span className="term-text">{term}</span>
        //         <button
        //           onClick={() => handleRemoveTerm(index)}
        //           className="remove-button"
        //         >
        //           x
        //         </button>
        //       </div>
        //     ))}
        //   </div>
        // </div>


        <div className="mac-book-pro142-group9">
            <img
                src="/external/rectangle147882-szgy-1400w.png"
                alt="Rectangle147882"
                className="mac-book-pro142-rectangle14"
            />
            {/* <img
                src="/external/rectangle427573-te7r-200h.png"
                alt="Rectangle427573"
                className="mac-book-pro142-rectangle42"
            /> */}


            {/* <div className="mac-book-pro142-group7-repeat-list">
                <div className="mac-book-pro142-group7">
                    <img
                        src="/external/rectangle43i781-2l8a-200h.png"
                        alt="Rectangle43I781"
                        className="mac-book-pro142-rectangle43"
                    />
                    <span className="mac-book-pro142-text02">
                        <span>term 1</span>
                    </span>
                </div>
                <div className="mac-book-pro142-group71">
                    <img
                        src="/external/rectangle43i781-967z-200h.png"
                        alt="Rectangle43I781"
                        className="mac-book-pro142-rectangle431"
                    />
                    <span className="mac-book-pro142-text04">
                        <span>term 1</span>
                    </span>
                </div>
            </div> */}


            <div className="mac-book-pro142-group8">

                <div className="mac-book-pro142-innerframe">
                    <input
                        type="text"
                        value={inputValue}
                        onChange={handleInputOnChange}
                        onKeyPress={handleInputOnKeyPress}
                        placeholder="Enter queryterms separated by semicolon"
                        className="input-field"
                    />
                    <button onClick={handleAddButtonClick} className="add-button">
                        Add
                    </button>
                </div>

                <div className="mac-book-pro142-group7-repeat-list">

                    {queryterms.map((term, index) => (
                        <div key={index} className="mac-book-pro142-group7">

                            <div key={index} className="term" >
                                <span>{term}</span>

                                <button
                                    onClick={() => handleRemoveTerm(index)}
                                    className="remove-button"
                                >
                                    x
                                </button>
                            </div>

                        </div>
                    ))}
                </div>




            </div>
            <div className="mac-book-pro142-donebutton" onClick={()=>setshowtextinput(false)}>
                <span className="mac-book-pro142-text12">
                    <span>done</span>
                </span>
            </div>
            <div className="mac-book-pro142-frame2"></div>
        </div>



    );
}

export default TextInput;
